package fr.formation.inti.entity;

import javax.persistence.Column;
import javax.persistence.Entity;
import javax.persistence.FetchType;
import javax.persistence.Id;
import javax.persistence.JoinColumn;
import javax.persistence.ManyToOne;
import javax.persistence.Table;
import javax.persistence.Transient;

/**
 * AudioGroupe generated by hbm2java
 */
@Entity
@Table(name = "audio_groupe", catalog = "bd_music_project")
public class AudioGroupe implements java.io.Serializable {
	
	private Integer audioId;
	private Groupe groupe;
	private String audioName;

	public AudioGroupe() {
	}

	public AudioGroupe(Integer audioId, Groupe groupe) {
		this.audioId = audioId;
		this.groupe = groupe;
	}

	public AudioGroupe(Integer audioId, Groupe groupe, String audioName) {
		this.audioId = audioId;
		this.groupe = groupe;
		this.audioName = audioName;
	}

	@Id
	@Column(name = "Audio_id", unique = true, nullable = false)
	public Integer getAudioId() {
		return this.audioId;
	}

	public void setAudioId(Integer audioId) {
		this.audioId = audioId;
	}

	@ManyToOne(fetch = FetchType.LAZY)
	@JoinColumn(name = "Groupe_id", nullable = false, updatable=false)
	public Groupe getGroupe() {
		return this.groupe;
	}

	public void setGroupe(Groupe groupe) {
		this.groupe = groupe;
	}

	@Column(name = "Audio_name", length = 200)

	
	@Transient
	public String getAudioPath() {
		if (audioName == null ||  groupe == null)
			return null;

		return "/groupe-audio/" +  groupe + "/" + audioName;
	}


}
